// Import the neccesary modules.
import bytes from "bytes";
import parseTorrent from "parse-torrent";
import path from "path";
import program from "commander";
import prompt from "prompt";
import torrentHealth from "torrent-tracker-health";

import Index from "./index";
import AnimeHelper from "./providers/helpers/animehelper";
import MovieHelper from "./providers/helpers/moviehelper";
import ShowHelper from "./providers/helpers/showhelper";
import packageJSON from "../package.json";
import Setup from "./config/setup";
import Util from "./util";

/** Class The class for the command line interface. */
export default class CLI {

  /**
   * Create a cli object.
   * @param {String} [providerName=CLI] - The default provider name.
   */
  constructor(providerName = "CLI") {
    /**
     * The util object with general functions.
     * @type {Util}
      */
    this._util = new Util();

    // Setup the CLI program.
    program
      .version(`${packageJSON.name} v${packageJSON.version}`)
      .option("-c, --content <type>", "Add content from the MongoDB database (anime | show | movie).", /^(anime)|^(show)|^(movie)/i, false)
      .option("-r, --run", "Run the API and start the scraping process.")
      .option("-s, --server", "Run the API without starting the scraping process.")
      .option("-e, --export <collection>", "Export a collection to a JSON file.", /^(anime)|^(show)|^(movie)/i, false)
      .option("-i, --import <collection>", "Import a JSON file to the database.");

    // Extra output on top of the default help output
    program.on("--help", () => {
      console.info("  Examples:");
      console.info("");
      console.info("    $ popcorn-api -c <anime|movie|show>");
      console.info("    $ popcorn-api --content <anime|movie|show>");
      console.info("");
      console.info("    $ popcorn-api -r");
      console.info("    $ popcorn-api --run");
      console.info("");
      console.info("    $ popcorn-api -s");
      console.info("    $ popcorn-api --server");
      console.info("");
      console.info("    $ popcorn-api -e <anime|movie|show>");
      console.info("    $ popcorn-api --export <anime|movie|show>");
      console.info("");
      console.info("    $ popcorn-api -i <path-to-json>");
      console.info("    $ popcorn-api --import <path-to-json>");
      console.info("");
    });

    // Parse the command line arguments.
    program.parse(process.argv);

    // The imdb property.
    const imdb = {
      description: "The imdb id of the show/movie to add (tt1234567)",
      type: "string",
      pattern: /^(tt\d{7}|)|^(.*)/i,
      message: "Not a valid imdb id.",
      required: true
    };

    // The Hummingbird id property.
    const hummingbirdId = {
      description: "The Hummingbird id of the anime to add",
      type: "string",
      pattern: /^(.*)/i,
      message: "Not a validHhummingbird id.",
      required: true
    };

    // The torrent property.
    const torrent = {
      description: "The link of the torrent to add",
      type: "string",
      message: "Not a valid torrent.",
      required: true
    };

    // The language property.
    const language = {
      description: "The language of the torrent to add (en, fr, jp)",
      type: "string",
      pattern: /^([a-zA-Z]{2})/i,
      message: "Not a valid language",
      required: true
    };

    // The quality property.
    const quality = {
      description: "The quality of the torrent (480p | 720p | 1080p)",
      type: "string",
      pattern: /^(480p|720p|1080p)/i,
      message: "Not a valid quality.",
      required: true
    };

    // The season property.
    const season = {
      description: "The season number of the torrent",
      type: "integer",
      pattern: /^(\d+)/i,
      message: "Not a valid season.",
      required: true
    };

    // The episode property.
    const episode = {
      description: "The episode number of the torrent",
      type: "integer",
      pattern: /^(\d+)/i,
      message: "Not a valid episode.",
      required: true
    };

    const confirm = {
      description: "Do you really want to import a collection, this can override the current data?",
      type: "string",
      pattern: /^(yes|no|y|n)$/i,
      message: "Type yes/no",
      required: true,
      default: "no"
    };

    /**
     * The shema used by `prompt` insert an anime show.
     * @type {Object}
     */
    this._animeSchema = {
      properties: {
        "imdb": hummingbirdId,
        "season": season,
        "episode": episode,
        "torrent": torrent,
        "quality": quality
      }
    };

    /**
     * The schema used by `prompt` insert a movie.
     * @type {Object}
     */
    this._movieSchema = {
      properties: {
        "imdb": imdb,
        "language": language,
        "torrent": torrent,
        "quality": quality
      }
    };

    /**
     * The schema used by `prompt` insert a show.
     * @type {Object}
     */
    this._showSchema = {
      properties: {
        "imdb": imdb,
        "season": season,
        "episode": episode,
        "torrent": torrent,
        "quality": quality
      }
    };

    /**
     * The schema used by `prompt` to confirm an import.
     * @type {Object}
     */
    this._importSchema = {
      properties: {
        "confirm": confirm
      }
    };
  };

  /** Adds a show to the database through the CLI. */
  _animePrompt() {
    prompt.get(this._showSchema, async(err, result) => {
      if (err) {
        console.error(`An error occurred: ${err}`);
        process.exit(1);
      } else {
        try {
          const { hummingbirdId, season, episode, quality, torrent } = result;
          const animeHelper = new AnimeHelper(providerName);
          const newAnime = await animeHelper.getHummingbirdInfo(hummingbirdId);
          if (newAnime && newAnime._id) {
            const data = await getShowTorrentDataRemote(torrent, quality, season, episode);
            await animeHelper.addEpisodes(newAnime, data, hummingbirdId);
            process.exit(0);
          }
        } catch (err) {
          console.error(`An error occurred: ${err}`);
          process.exit(1);
        }
      }
    });
  };

  /**
   * Get movie data from a given torrent url.
   * @param {String} torrent - The url of the torrent.
   * @param {String} language - The language of the torrent.
   * @param {String} quality - The quality of the torrent.
   * @returns {Promise} - Movie data from the torrent.
   */
  _getMovieTorrentDataRemote(torrent, language, quality) {
    return new Promise((resolve, reject) => {
      parseTorrent.remote(torrent, (err, result) => {
        if (err) return reject(err);

        const magnet = parseTorrent.toMagnetURI(result);
        torrentHealth(magnet).then(res => {
          const { seeds, peers } = res;
          const data = {};
          if (!data[language]) data[language] = {};
          if (!data[language][quality]) data[language][quality] = {
            url: magnet,
            seed: seeds,
            peer: peers,
            size: result.length,
            filesize: bytes(result.length),
            provider: providerName
          };
          return resolve(data);
        }).catch(err => reject(err));
      });
    });
  };

  /** Adds a movie to the database through the CLI. */
  _moviePrompt() {
    prompt.get(this._movieSchema, async(err, result) => {
      if (err) {
        console.error(`An error occurred: ${err}`);
        process.exit(1);
      } else {
        try {
          const { imdb, quality, language, torrent } = result;
          const movieHelper = new MovieHelper(providerName);
          const newMovie = await movieHelper.getTraktInfo(imdb);
          if (newMovie && newMovie._id) {
            const data = await getMovieTorrentDataRemote(torrent, language, quality);
            await movieHelper.addTorrents(newMovie, data);
            process.exit(0);
          }
        } catch (err) {
          console.error(`An error occurred: ${err}`);
          process.exit(1);
        }
      }
    });
  };

  /**
   * Get show data from a given torrent url.
   * @param {String} torrent - The url of the torrent.
   * @param {String} quality - The quality of the torrent.
   * @param {Integer} season - The season of the show from the torrent file.
   * @param {Integer} episode - The episode of the show from the torrent.
   * @returns {Promise} - Show data from the torrent.
   */
  _getShowTorrentDataRemote(torrent, quality, season, episode) {
    return new Promise((resolve, reject) => {
      parseTorrent.remote(torrent, (err, result) => {
        if (err) return reject(err);

        const magnet = parseTorrent.toMagnetURI(result);
        torrentHealth(magnet).then(res => {
          const { seeds, peers } = res;
          const data = {};
          if (!data[season]) data[season] = {};
          if (!data[season][episode]) data[season][episode] = {};
          if (!data[season][episode][quality]) data[season][episode][quality] = {
            url: magnet,
            seeds,
            peers,
            provider: providerName
          };
          return resolve(data);
        }).catch(err => reject(err));
      });
    });
  };

  /** Adds a show to the database through the CLI. */
  _showPrompt() {
    prompt.get(this._showSchema, async(err, result) => {
      if (err) {
        console.error(`An error occurred: ${err}`);
        process.exit(1);
      } else {
        try {
          const { imdb, season, episode, quality, torrent } = result;
          const showHelper = new ShowHelper(providerName);
          const newShow = await showHelper.getTraktInfo(imdb);
          if (newShow && newShow._id) {
            const data = await getShowTorrentDataRemote(torrent, quality, season, episode);
            await showHelper.addEpisodes(newShow, data, imdb);
            process.exit(0);
          }
        } catch (err) {
          console.error(`An error occurred: ${err}`);
          process.exit(1);
        }
      }
    });
  };

  /** Confimation to import a collection */
  _importPrompt() {
    prompt.get(this._importSchema, (err, result) => {
      if (err) {
        console.error(`An error occured: ${err}`);
        process.exit(1);
      } else {
        if (result.confirm.match(/^(y|yes)/i)) {
          let collection = path.basename(program.import);
          const index = collection.lastIndexOf(".");
          collection = collection.substring(0, index);
          this._util.importCollection(collection, program.import)
            .catch(err => console.error(err));
        } else if (result.confirm.match(/^(n|no)/i)) {
          process.exit(0);
        }
      }
    });
  };

  /** Run the CLI program. */
  run() {
    if (program.run) {
      new Index({
        start: true,
        pretty: true,
        verbose: false,
        debug: false
      });
    } else if (program.server) {
      new Index({
        start: false,
        pretty: true,
        verbose: false,
        debug: false
      });
    } else if (program.content) {
      prompt.start();
      Setup.connectMongoDB();

      if (program.content.match(/^(show)/i)) {
        this._showPrompt();
      } else if (program.content.match(/^(movie)/i)) {
        this._moviePrompt();
      }
    } else if (program.export) {
      this._util.exportCollection(program.export);
    } else if (program.import) {
      this._importPrompt();
    } else {
      console.error("\n  \x1b[31mError:\x1b[36m No valid command given. Please check below:\x1b[0m");
      program.help();
    }
  };

};

// Import the neccesary modules.
import ExpressWinston from "express-winston";
import fs from "fs";
import path from "path";
import sprintf from "sprintf";
import Winston from "winston";

import { tempDir } from "./constants";
import { name } from "../../package.json";

/** Class for configuring logging. */
export default class Logger {

  /**
   * Create a logger object.
   * @param {?Boolean} [verbose] - Debug mode for no output.
   * @param {?Boolean} [debug] - Debug mode for extra output.
   */
  constructor(pretty, verbose) {
    /**
     * Pretty mode.
     * @type {Boolean}
     */
    Logger._pretty = pretty;

    /**
     * Verbose mode.
     * @type {Boolean}
     */
    Logger._verbose = verbose;

     // Create the temp directory if it does not exists.
    if (!fs.existsSync(tempDir)) fs.mkdirSync(tempDir);

    /**
     * The log levels Winston will be using.
     * @type {Object}
     */
    Logger._levels = {
      error: 0,
      warn: 1,
      info: 2,
      debug: 3
    };

    if (Logger._pretty) {
      /**
       * The Winston instance.
       * @external {Winston} https://github.com/winstonjs/winston
       */
      Logger.logger = new Winston.Logger({
        transports: [
          new Winston.transports.Console({
            name,
            levels: Logger._levels,
            formatter: Logger._consoleFormatter,
            handleExceptions: true,
            prettyPrint: true
          }),
          new Winston.transports.File({
            filename: path.join(tempDir, `${name}.log`),
            json: false,
            level: "warn",
            formatter: Logger._fileFormatter,
            maxsize: 5242880,
            handleExceptions: true
          })
        ],
        exitOnError: false
      });

      /**
       * The Express Winston instance.
       * @external {ExpressWinston} http://bithavoc.io/express-winston/
       */
      Logger.expressLogger = new ExpressWinston.logger({
        winstonInstance: Logger.logger,
        expressFormat: true
      });
    }

    // Create the logger object.
    Logger._createLogger(this._pretty);
  };

  /**
   * Check if the message is empty and replace it with the meta.
   * @param {Object} args - Arguments passed by Winston.
   * @returns {Object} - Formatter arguments passed by Winston.
   */
  static _checkEmptyMessage(args) {
    if (args.message === "" && Object.keys(args.meta).length !== 0)
      args.message = JSON.stringify(args.meta);

    return args;
  };

  /**
   * Get the color of the output based on the log level.
   * @param {String} level - The log level.
   * @returns {String} - A color based on the log level.
   */
  static _getLevelColor(level) {
    switch (level) {
    case "error":
      return "\x1b[31m";
      break;
    case "warn":
      return "\x1b[33m";
      break;
    case "info":
      return "\x1b[36m";
      break;
    case "debug":
      return "\x1b[34m";
      break;
    default:
      return "\x1b[36m";
      break;
    }
  };

  /**
   * Formatter function which formats the output to the console.
   * @param {Object} args - Arguments passed by Winston.
   * @returns {String} - The formatted message.
   */
  static _consoleFormatter(args) {
    args = Logger._checkEmptyMessage(args);
    const color = Logger._getLevelColor(args.level);

    return sprintf(`\x1b[0m[%s] ${color}%5s:\x1b[0m %2s/%d: \x1b[36m%s\x1b[0m`,
      new Date().toISOString(), args.level.toUpperCase(), name,
      process.pid, args.message);
  };

  /**
   * Formatter function which formate the output to the log file.
   * @param {Object} args - Arguments passed by Winston.
   * @returns {String} - The formatted message.
   */
  static _fileFormatter(args) {
    args = Logger._checkEmptyMessage(args);
    return JSON.stringify({
      name,
      pid: process.pid,
      level: args.level,
      msg: args.message,
      time: new Date().toISOString()
    });
  };

  /**
   * Function to create a global logger object based on the properties of the Logger class.
   */
  static _createLogger() {
    if (!global.logger) global.logger = {};

    Object.keys(Logger._levels).map(level => {
      if (Logger._pretty) {
        global.logger[level] = msg => {
          if (!Logger._verbose) Logger.logger[level](msg);
        };
      } else {
        global.logger[level] = msg => {
          if (!Logger._verbose) console[level](msg);
        };
      }
    });
  };

};

// Import the neccesary modules.
import Index from "../controllers/index";
import Animes from "../controllers/animes";
import Movies from "../controllers/movies";
import Shows from "../controllers/shows";
import Exports from "../controllers/exports";

/** Class for setting up the routes for the API. */
export default class Routes {

  /**
   * Create a routes object.
   * @param {Express} app - The ExpresssJS instance.
   */
  constructor(app) {
    /**
     * The index controller.
     * @type {Index}
     */
    Routes.index = new Index();

    /**
     * The animes controller.
     * @type {Animes}
     */
    Routes._animes = new Animes();

    /**
     * The movies controller.
     * @type {Movies}
     */
    Routes._movies = new Movies();

    /**
     * The shows controller.
     * @type {Shows}
     */
    Routes._shows = new Shows();

    /**
     * The exports controller.
     * @type {Exports}
     */
    Routes._exports = new Exports();

    Routes._setupRoutes(app);
  };

  /**
   * Setup ExpressJS routing.
   * @param {ExpressJS} app - The ExpresssJS application.
   */
  static _setupRoutes(app) {
    app.get("/", Routes.index.getIndex);
    app.get("/logs/error", Routes.index.getErrorLog);

    app.get("/animes", Routes._animes.getAnimes);
    app.get("/animes/:page", Routes._animes.getPage);
    app.get("/anime/:id", Routes._animes.getAnime);
    app.get("/random/anime", Routes._animes.getRandomAnime);

    app.get("/movies", Routes._movies.getMovies);
    app.get("/movies/:page", Routes._movies.getPage);
    app.get("/movie/:id", Routes._movies.getMovie);
    app.get("/random/movie", Routes._movies.getRandomMovie);

    app.get("/shows", Routes._shows.getShows);
    app.get("/shows/:page", Routes._shows.getPage);
    app.get("/show/:id", Routes._shows.getShow);
    app.get("/random/show", Routes._shows.getRandomShow);

    app.get("/exports/:collection", Routes._exports.getExport);
  };

};

// Import the neccesary modules.
import bodyParser from "body-parser";
import compress from "compression";
import mongoose from "mongoose";
import responseTime from "response-time";

import Logger from "./logger";
import { dbHosts, dbName, Promise } from "./constants";

/** Class for setting up the API. */
export default class Setup {

  /**
   * Setup the Express service.
   * @param {Express} app - The ExpresssJS instance.
   * @param {?Boolean} [pretty] - Pretty output with Winston logging.
   * @param {?Boolean} [verbose] - Debug mode for no output.
   */
  constructor(app, pretty, verbose) {
    // Used to extract data from query strings.
    RegExp.escape = text => text.replace(/[-[\]{}()*+?.,\\^$|#\s]/g, "\\$&");

    // Connection and configuration of the MongoDB database.
    Setup.connectMongoDB();

    // Enable parsing URL encoded bodies.
    app.use(bodyParser.urlencoded({extended: true}));

    // Enable parsing JSON bodies.
    app.use(bodyParser.json());

    // Enables compression of response bodies.
    app.use(compress({
      threshold: 1400,
      level: 4,
      memLevel: 3
    }));

    // Enable response time tracking for HTTP request.
    app.use(responseTime());

    // Enable HTTP request logging.
    if (pretty && !verbose) app.use(Logger.expressLogger);
  }

  /** Connection and configuration of the MongoDB database. */
  static connectMongoDB() {
    mongoose.Promise = Promise;
    mongoose.connect(`mongodb://${dbHosts.join(",")}/${dbName}`, {
      db: {
        native_parser: true
      },
      replset: {
        rs_name: "pt0",
        connectWithNoPrimary: true,
        readPreference: "nearest",
        strategy: "ping",
        socketOptions: {
          keepAlive: 1
        }
      },
      server: {
        readPreference: "nearest",
        strategy: "ping",
        socketOptions: {
          keepAlive: 1
        }
      }
    });
  };

};

// Import the neccesary modules.
import Anime from "../models/Anime";
import { pageSize } from "../config/constants";

/** Class for getting anime data from the MongoDB. */
export default class Animes {

  /** Create an animes object. */
  constructor() {
    /**
     * Object used for the projection of anime shows.
     * @type {Object}
     */
    Animes._projection = {
      images: 1,
      mal_id: 1,
      haru_id: 1,
      tvdb_id: 1,
      imdb_id: 1,
      slug: 1,
      title: 1,
      year: 1,
      type: 1,
      item_data: 1,
      rating: 1,
      genres: 1,
      num_seasons: 1
    };
  };

  /**
   * Get all the pages.
   * @param {Request} req - The express request object.
   * @param {Response} res - The express response object.
   * @param {Function} next - The next function for Express.
   * @returns {String[]} - A list of pages which are available.
   */
  getAnimes(req, res, next) {
    return Anime.count({
      num_seasons: {
        $gt: 0
      }
    }).exec().then(count => {
      const pages = Math.round(count / pageSize);
      const docs = [];

      for (let i = 1; i < pages + 1; i++) docs.push(`animes/${i}`);

      return res.json(docs);
    }).catch(err => next(err));
  };

  /**
   * Get one page.
   * @param {Request} req - The express request object.
   * @param {Response} res - The express response object.
   * @param {Function} next - The next function for Express.
   * @returns {Anime[]} - The contents of one page.
   */
  getPage(req, res, next) {
    const page = req.params.page - 1;
    const offset = page * pageSize;

    if (req.params.page.match(/all/i)) {
      return Anime.aggregate([{
          $match: {
            num_seasons: {
              $gt: 0
            }
          }
        }, {
          $project: Animes._projection
        }, {
          $sort: {
            title: -1
          }
        }]).exec()
        .then(docs => res.json(docs))
        .catch(err => next(err));
    } else {
      const query = {
        num_seasons: {
          $gt: 0
        }
      };
      const data = req.query;

      if (!data.order) data.order = -1;

      let sort = {
        "rating.votes": parseInt(data.order, 10),
        "rating.percentage": parseInt(data.order, 10),
        "rating.watching": parseInt(data.order, 10)
      };

      if (data.keywords) {
        const words = data.keywords.split(" ");
        let regex = "^";
        for (let w in words) regex += `(?=.*\\b${RegExp.escape(words[w].toLowerCase())}\\b)`;
        query.title = {
          $regex: new RegExp(`${regex}.*`),
          $options: "gi"
        };
      }

      if (data.sort) {
        if (data.sort.match(/name/i)) sort = {
          "title": (parseInt(data.order, 10) * -1)
        };
        if (data.sort.match(/rating/i)) sort = {
          "rating.percentage": parseInt(data.order, 10),
          "rating.votes": parseInt(data.order, 10)
        };
        // if (data.sort === "trending") sort = {
        //   "rating.watching": parseInt(data.order, 10)
        // };
        // if (data.sort === "updated") sort = {
        //   "latest_episode": parseInt(data.order, 10)
        // };
        if (data.sort.match(/year/i)) sort = {
          "year": parseInt(data.order, 10)
        };
      }

      if (data.genre && !data.genre.match(/all/i)) query.genres = data.genre;

      return Anime.aggregate([{
          $sort: sort
        }, {
          $match: query
        }, {
          $project: Animes._projection
        }, {
          $skip: offset
        }, {
          $limit: pageSize
        }]).exec()
        .then(docs => res.json(docs))
        .catch(err => next(err));
    }
  };

  /**
   * Get info from one anime.
   * @param {Request} req - The express request object.
   * @param {Response} res - The express response object.
   * @param {Function} next - The next function for Express.
   * @returns {Anime} - The details of a single anime.
   */
  getAnime(req, res, next) {
    return Anime.findOne({
        _id: req.params.id
      }, {
        latest_episode: 0
      }).exec()
      .then(docs => res.json(docs))
      .catch(err => next(err));
  };

  /**
   * Get a random anime.
   * @param {Request} req - The express request object.
   * @param {Response} res - The express response object.
   * @param {Function} next - The next function for Express.
   * @returns {Anime} - A random movie.
   */
  getRandomAnime(req, res, next) {
    return Anime.aggregate([{
        $project: Animes._projection
      }, {
        $sample: {
          size: 1
        }
      }, {
        $limit: 1
      }]).exec()
