import fs from "fs";
import path from "path";

import Anime from "../models/Anime";
import Movie from "../models/Movie";
import Show from "../models/Show";
import Util from "../util";

import { server, statusFile, tempDir, updatedFile } from "../config/constants";
import { name, repository, version } from "../../package.json";

/** class for displaying information about the server the API is running on. */
export default class Index {

  /** Create an index object. */
  constructor() {
    /**
     * The util object with general functions.
     * @type {Util}
     */
    Index._util = new Util();
  };

  /**
   * Displays a given file.
   * @param {Request} req - The express request object.
   * @param {Response} res - The express response object.
   * @param {String} path - The path to the file.
   * @param {String} file - The name of the file.
   * @returns {Object} - A file to display in the browser.
   */
  static _displayFile(req, res, root, file) {
    if (fs.existsSync(path.join(root, file))) {
      return res.sendFile(file, {
        root,
        headers: {
          "Content-Type": "text/plain; charset=UTF-8"
        }
      });
    } else {
      return res.json({error: `Could not find file: '${root}'`});
    }
  };

  /**
   * Get general information about the server.
   * @param {Request} req - The express request object.
   * @param {Response} res - The express response object.
   * @param {Function} next - The next function for Express.
   * @returns {Object} - General information about the server.
   */
  async getIndex(req, res, next) {
    try {
      const { updated } = JSON.parse(fs.readFileSync(path.join(tempDir, updatedFile), "utf8")),
        { status } = JSON.parse(fs.readFileSync(path.join(tempDir, statusFile), "utf8")),
        commit = await Index._util.executeCommand("git rev-parse --short HEAD"),
        totalAnimes = await Anime.count({num_episodes: {$gt: 0}}).exec(),
        totalMovies = await Movie.count().exec(),
        totalShows = await Show.count({num_seasons: {$gt: 0}}).exec();

      return res.json({
        repo: repository.url, server, status,
        totalAnimes, totalMovies, totalShows,
        updated, uptime: process.uptime() | 0,
        version, commit
      });
    } catch (err) {
      return next(err);
    }
  };

  /**
   * Displays the 'popcorn-api.log' file.
   * @param {Request} req - The express request object.
   * @param {Response} res - The express response object.
   * @returns {File} - The content of the log file.
   */
  getErrorLog(req, res) {
    return Index._displayFile(req, res, tempDir, `${name}.log`);
  };

};

// Import the neccesary modules.
import Movie from "../models/Movie";
import { pageSize } from "../config/constants";

/** Class for getting movie data from the MongoDB. */
export default class Movies {

  /** Create a movies object. */
  constructor() {
    /**
     * Object used for the projection of movies.
     * @type {Object}
     */
    Movies._projection = {
      _id: 1,
      imdb_id: 1,
      title: 1,
      year: 1,
      runtime: 1,
      images: 1,
      genres: 1,
      synopsis: 1,
      trailer: 1,
      certification: 1,
      released: 1,
      rating: 1,
      torrents: 1
    };
  };

  /**
   * Get all the pages.
   * @param {Request} req - The express request object.
   * @param {Response} res - The express response object.
   * @param {Function} next - The next function for Express.
   * @returns {Array} - A list of pages which are available.
   */
  getMovies(req, res, next) {
    return Movie.count().exec().then(count => {
      const pages = Math.round(count / pageSize);
      const docs = [];

      for (let i = 1; i < pages + 1; i++) docs.push(`movies/${i}`);

      return res.json(docs);
    }).catch(err => next(err));
  };

  /**
   * Get one page.
   * @param {Request} req - The express request object.
   * @param {Response} res - The express response object.
   * @param {Function} next - The next function for Express.
   * @returns {Array} - The contents of one page.
   */
  getPage(req, res, next) {
    const page = req.params.page - 1;
    const offset = page * pageSize;

    if (req.params.page.match(/all/i)) {
      return Movie.aggregate([{
          $project: Movies._projection
        }, {
          $sort: {
            title: -1
          }
        }]).exec()
        .then(docs => res.json(docs))
        .catch(err => next(err));
    } else {
      const query = {};
      const data = req.query;

      if (!data.order) data.order = -1;

      let sort = {
        "rating.votes": parseInt(data.order, 10),
        "rating.percentage": parseInt(data.order, 10),
        "rating.watching": parseInt(data.order, 10)
      };

      if (data.keywords) {
        const words = data.keywords.split(" ");
        let regex = "^";
        for (let w in words) regex += `(?=.*\\b${RegExp.escape(words[w].toLowerCase())}\\b)`;
        query.title = {$regex: new RegExp(`${regex}.*`), $options: "gi"};
      }

      if (data.sort) {
        if (data.sort.match(/last added/i)) sort = {
          "released": parseInt(data.order, 10)
        };
        if (data.sort.match(/rating/i)) sort = {
          "rating.percentage": parseInt(data.order, 10),
          "rating.votes": parseInt(data.order, 10)
        };
        if (data.sort.match(/title/i)) sort = {
          "title": (parseInt(data.order, 10) * 1)
        };
        if (data.sort.match(/trending/i)) sort = {
          "rating.watching": parseInt(data.order, 10)
        };
        if (data.sort.match(/year/i)) sort = {
          "year": parseInt(data.order, 10)
        };
      }

      if (data.genre && !data.genre.match(/all/i)) {
        if (data.genre.match(/science[-\s]fiction/i) || data.genre.match(/sci[-\s]fi/i)) data.genre = "science-fiction";
        query.genres = data.genre.toLowerCase();
      }

      return Movie.aggregate([{
          $sort: sort
        }, {
          $match: query
        }, {
          $project: Movies._projection
        }, {
          $skip: offset
        }, {
          $limit: pageSize
        }]).exec()
        .then(docs => res.json(docs))
        .catch(err => next(err));
    }
  };

  /**
   * Get info from one movie.
   * @param {Request} req - The express request object.
   * @param {Response} res - The express response object.
   * @param {Function} next - The next function for Express.
   * @returns {Movie} - The details of a single movie.
   */
  getMovie(req, res, next) {
    return Movie.aggregate([{
        $match: {
          _id: req.params.id
        }
      }, {
        $project: Movies._projection
      }, {
        $limit: 1
      }]).exec()
      .then(docs => res.json(docs[0]))
      .catch(err => next(err));
  };

  /**
   * Get a random movie.
   * @param {Request} req - The express request object.
   * @param {Response} res - The express response object.
   * @param {Function} next - The next function for Express.
   * @returns {Movie} - A random movie.
   */
  getRandomMovie(req, res, next) {
    return Movie.aggregate([{
        $project: Movies._projection
      }, {
        $sample: {
          size: 1
        }
      }, {
        $limit: 1
      }]).exec()
      .then(docs => res.json(docs[0]))
      .catch(err => next(err));
  };

};

